css:
main .product__buy .btn-lg {
    padding: 0px;
}
main .products-carousel__title {
    font-family: system-ui;
    font-weight: 700;
}

/*preço*/
main .product-card .prices__value {
    font-size: 20px;
    margin: 10px 0 10px 0;
}
/*remover vão acima cabeçalho*/
.header {
    top: -1px;
}

/*texto vantagens*/
main .info-bar__item div .info-bar__title {
    font-family: inherit;
    font-size: 16px;
    font-style: normal;
    color: #000000;
}
.rounded-icon {
    border: 1px solid;
    margin-right: 6px;
    color: #ff0d72;
}

/*cor fontes*/
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    color: #000000;
}
/*tamanho fonte titulo produto*/
main .product-card__name {
    font-size: 14px;
    font-weight: 500;
}
.product-card__name {
    --max-lines: 2;
}

.alert {
    border-radius: 10px;
    margin-bottom: 0;
    color: black;
    background-color: #00ff8e;
    font-weight: 600;
}
