css:
main .product__buy .btn-lg {
    padding: 0px;
}
main .products-carousel__title {
    font-family: system-ui;
    font-weight: 700;
    padding: 20px 0 0px 0;
    font-size: 22px;
}
/*preço*/
main .product-card .prices__value {
    font-size: 18px;
    margin: 10px 0 10px 0;
}
/*texto vantagens*/
main .info-bar__item div .info-bar__title {
    font-family: inherit;
    font-size: 16px;
    font-style: normal;
    color: #000000;
}
.rounded-icon {
    border: 1px solid;
    margin-right: 6px;
    color: #ff0d72;
}
/*cor fontes*/
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    color: #000000;
}
/*tamanho fonte titulo produto*/
main .product-card__name {
    font-size: 14px;
    font-weight: 500;
}
.product-card__name {
    --max-lines: 2;
}
.alert {
    border-radius: 10px;
    margin-bottom: 0;
    color: black;
    background-color: #00ff8e;
    font-weight: 600;
}
/*fixar botao comprar*/
.product-card__buy.fade {
  opacity: 1 !important;
}
.product-card--small .product-card__buy {
    position: initial;
    right: 0;
}
/*botao comprar*/
.btn-primary {
    font-weight: 600;
    font-size: 13px;
    padding: 10px;
}
button.btn.btn-lg.btn-primary {
    font-size: 15px;
}
i.i-shopping-bag.mr-1 {
    font-weight: 600;
}
/*circulo quantidade adicionada carrinho*/
main .header__buttons .badge-primary {
    background-color: #ff0d72;
}
li.glide__slide.brands-carousel__item {
    max-width: 100px;
    margin-right: 92px !important;
}
/*ajustes menu*/
.header__nav .header__submenu a {
    color: #ff0d72;
    font-size: 13px;
    padding: 5px 0px;
}
div>.header__nav .header__category:hover {
    color: #ff0d72;
}
div>.header__nav .header__category {
    font-size: 13px;
}
/*ajustes botao comprar motor de busca*/
.btn-sm {
    padding: 10px 45px;
}
@media screen and (max-width: 768px) {
  .btn-sm {
    padding: 10px 15px;
  }
}
div>.header__nav--full a {
    padding: 0 16px;
    margin-top: 0px;
}
.menu__item {
   font-weight: 500;
}
/*altura linha menu*/
div>.header__nav--full {
    padding-top: 20px;
}
div>.header__nav--full:before {
    margin-top: -8px;
}
/*fundo pesquisa*/
.header__search-input {
    background: #0000000a;
}
main .header__search {
    border-bottom: 0;
    background: 0;
}
/*espaço superior menu*/
.header__container {
    margin-bottom: 0;
}
/*linha horizontal estante produts*/
main .products-carousel__title:before {
    display: inline;
    position: inline;
    background: #00000061;
    height: 1px;
    left: 0%;
    width: 100%;
    margin-top: 10px;
}
.menu__link {
    background: #ff0d72;
    border-radius: 10px;
    font-weight: 500;
    color: #ffffff;
    border-top: 0;
}
.gallery__thumbs {
    margin: 0 !important;
}
.quantity-selector__label {
    margin-left: 0 !important;
}
/*
main .product__buy .quantity-selector__btn-container {
    margin-left: 0 !important;
}*/
/*seletor de quantidade pagina produto mobile*/
@media (max-width: 575.98px){
main.product__buy .quantity-selector__btn-container {
    display: inherit !important;
    margin-left: 0px !important;
    border-radius: 10px !important;
  }
  button.btn.btn-lg.btn-primary {
    max-width: 100%;
  }
}
@media (max-width: 575.98px){
main .product__buy .quantity-selector__btn-container {
    display: inherit;
}
}
