css:
/* --- Títulos e Tipografia Base --- */
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, 
main .product-card__name, 
main .page-title__head h1,
.shelfs-nav__title,
main .products-carousel__title,
main .info-bar__item div .info-bar__title {
    font-family: system-ui, -apple-system, sans-serif !important;
    color: #000000;
}

/* --- Cores de Marca (Rosa) --- */
/* Unificando tudo que usa o rosa padrão da Miga sua Make */
main .product .variations__option:focus, 
main .product .variations__option:hover,
main .product .variations__option--selected:not(.disabled),
main .product-card__offer-stamp,
main .header__buttons .badge-primary,
.header__nav .header__submenu a,
div>.header__nav .header__category:hover,
.menu__link,
a.list-group-item.list-group-item-action.active {
    background-color: #ff0d72 !important;
    border-color: #ff0d72 !important;
    color: #ffffff !important;
}

/* --- Padronização de Preços --- */
/* Unifiquei cores e pesos que estavam repetidos em blocos separados */
main .product-card .prices__value,
main .product-card .prices__discount,
main .product-card .prices__installments,
main .product .prices__value,
.prices--literal .prices__discount span,
main .product__name {
    color: #303030;
    font-weight: 500;
}

/* Destaques específicos de preço */
main .product-card .prices__discount, 
main .product-card .prices__installments {
    font-size: 18px;
    font-weight: 700;
}

/* --- Botões e Interações --- */
.btn-primary, .btn-success, .btn-sm, .btn-lg {
    border-radius: 10px;
    font-weight: 500;
}

/* Ajustes específicos de botões que não podem ser globais */
button.btn.btn-lg.btn-primary { font-size: 15px; }
.btn-success { width: 100%; text-transform: uppercase; line-height: 36px; background-color: #00ff8e; color: #000; }
a.cart__btn-checkout.btn.btn-lg.btn-block.btn-success { text-transform: capitalize; font-size: 18px; }

/* --- Checkout e Listas --- */
a.payment__gateway.list-group-item.list-group-item-action,
.list-group-item-action.list-group-item {
    background-color: #f0f0f0;
    margin: 6px 0;
    border: 0;
    border-radius: 8px;
    color: #000;
}

/* --- Ocultar Elementos --- */
/* Agrupado para o navegador processar uma única regra de 'display none' */
main .product .prices__compare small:not(.prices__discount-label),
.product-card__buy.fade,
.product__short-stock,
.product__free-shipping-from,
main .product #product-actions>div article,
.summary__amount__row>span small {
    display: none !important;
}

/* --- Ajustes de Layout e Espaçamento --- */
/* Mantidos conforme original para não quebrar o visual atual */
.product-card { padding: 10px; }
.header__container { margin-bottom: 0; max-width: 100%; }
.row.no-gutters { margin: 20px; }

/* --- Mobile / Responsivo --- */
@media screen and (max-width: 768px) {
    .btn-sm { padding: 10px 15px; }
}

@media (max-width: 575.98px){
    main .product__buy .quantity-selector__btn-container {
        display: inherit;
        margin-left: 0;
        margin-right: 10px;
        border-radius: 10px;
        height: 55px;
    }
    main .product__buy .btn-lg { height: 55px; }
    .quantity-selector__input { padding-top: 20px; }
}

@media (min-width: 768px){
    .page-title__head { display: block; }
    .page-title.container.mt-4.mb-4.mb-lg-5 { padding-top: 50px; }
    .col-12.col-lg-2.mb-3.mb-lg-0 { max-width: 12%; }
}
/*esconder marcas*/
.glide__slide.brands-carousel__item:has(a[href="/lanffy"]),
.glide__slide.brands-carousel__item:has(a[href="/tango"]),
.glide__slide.brands-carousel__item:has(a[href="/playboy"]),
.glide__slide.brands-carousel__item:has(a[href="/miss-rose-makeup"]),
.glide__slide.brands-carousel__item:has(a[href="/miss-lary"]),
.glide__slide.brands-carousel__item:has(a[href="/vizzela"]),
.glide__slide.brands-carousel__item:has(a[href="/makiaj"]),
.glide__slide.brands-carousel__item:has(a[href="/sarahs-beauty"]),
.glide__slide.brands-carousel__item:has(a[href="/dhermosa"]),
.glide__slide.brands-carousel__item:has(a[href="/creamy-skincare"])
{
    display: none !important;
}
