css:
/*aumentar icones cabeçalho*/
.header__buttons .btn {
    padding: 0px 10px 0px 10px;
    font-size: 28px;
}
/*espaçamento de letras*/
body {
    letter-spacing: 0px;
}

/*fixar botão comprar*/
.product-card__buy.fade {
  opacity: 1 !important;
}

/*mudar cor botão comprar*/
.product__buy button {
color: #ffffff;
background-color: #57cc86;
border-color: #57cc86;
width: 100%;
}
.product__buy button:hover {
background: #4e7d02;
border-color: #4e7d02;
}
