css:
/*aumentar icones cabeçalho*/
.header__buttons .btn {
    padding: 0px 10px 0px 10px;
    font-size: 28px;
}
/*espaçamento de letras*/
body {
    letter-spacing: 0px;
}

/*fixar botão comprar*/
.product-card__buy.fade {
  opacity: 1 !important;
}

/*espessura letra menu lateral*/
.menu__list {
    font-weight: 600;
    font-size: 18px;
}

/*fundo categorias lista menu*/
.menu__item {
    background: #0000000a;
    border-radius: 10px;
    margin: 3px 5px 0px 5px;
}

/*ajustes botao comprar*/
.btn-primary {
    padding: 8px;
    border-radius: 10px;
    font-size: 13px !important;
}
/*linhas por titulo produto*/
.product-card__name {
  --max-lines: 2;
}
/*espaçamento textos*/
h1, h2, h3, h4, h5, h6 {
  letter-spacing: 0px;
  text-transform: none;
}
