css:
main .product__buy .btn-lg {
    padding: 0px;
}
main .products-carousel__title {
    font-family: system-ui;
    font-weight: 400;
    padding: 20px 0 10px 0;
    font-size: 20px;
}
/*sombra card do produto*/
.product-card {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, .15);
}
/*preço*/
main .product-card .prices__value {
    font-size: 14px;
    font-weight: 600;
    margin-right: 150px;
}
main .product-card .prices__discount, main .product-card .prices__installments {
    color: var(--primary);
    font-size: 18px;
    font-weight: 700;
}
.product-card__prices {
    flex-wrap: wrap;
    align-items: center;
}
.prices:not(.prices--big) .prices__installments {
    font-weight: var(--font-light);
    font-size: smaller;
    color: #4f4f4f;
}
.prices--literal .prices__discount span {
    font-size: 28px;
    color: #ff0d72;
}
main .product .prices__value {
    font-size: 20px;
    font-weight: 600;
}
.prices__discount {
    padding: 8px 0;
}
.product-card__prices .prices__installments:after {
  content: ' sem juros';
}
main .product .prices__compare small:not(.prices__discount-label) {
    display: none;
}
main .product .prices__compare+strong {
    margin-left: 0;
}
small.prices__discount-label {
    font-size: 12px;
}
/*tag desconto*/
main .product-card__offer-stamp {
    background-color: #000000;
    border-radius: 6px;
    min-width: 44px;
    font-size: 12px;
    font-family: 'Open Sans';
}
.product-card__offer-stamp i {
    font-size: 140%;
    opacity: 1;
}
/*texto vantagens*/
main .info-bar__item div .info-bar__title {
    font-family: inherit;
    font-size: 14px;
    font-weight: 600;
    font-style: normal;
    color: #000000;
}

/*barra vantagens*/
main .info-bar {
    background: #f7f7f7;
}

.rounded-icon {
    border: 2px solid;
    margin-right: 6px;
    color: #ff0d72;
}
/*cor fontes*/
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    color: #000000;
}
/*tamanho fonte titulo produto*/
main .product-card__name {
    font-size: 15px;
    font-weight: 400;
}
.product-card__name {
    --max-lines: 2;
}
.alert {
    border-radius: 10px;
    margin-bottom: 0;
    color: black;
    background-color: #00ff8e;
    font-weight: 600;
}
/*fixar botao comprar*/
.product-card__buy.fade {
  opacity: 1 !important;
}
.product-card--small .product-card__buy {
    position: initial;
    right: 0;
}
/*botao comprar*/
.btn-primary {
    font-weight: 500;
    font-size: 11px;
    padding: 8px;
}
button.btn.btn-lg.btn-primary {
    font-size: 15px;
}
i.i-shopping-bag.mr-1 {
    font-weight: 600;
}
/*circulo quantidade adicionada carrinho*/
main .header__buttons .badge-primary {
    background-color: #ff0d72;
}

/*tamanho marcas*/
img.img-fluid {
    max-width: 50%;
}

/*ajustes menu*/
.header__nav .header__submenu a {
    color: #ff0d72;
    font-size: 13px;
    padding: 5px 0px;
}
div>.header__nav .header__category:hover {
    color: #ff0d72;
}
div>.header__nav .header__category {
    font-size: 13px;
}
/*ajustes botao comprar motor de busca*/
.btn-sm {
    padding: 10px 45px;
}
main .product__buy .quantity-selector__btn-container{
  margin-left: 0;
}
@media screen and (max-width: 768px) {
  .btn-sm {
    padding: 10px 15px;
  }
}
div>.header__nav--full a {
    padding: 0 20px;
    margin-top: 0px;
}
.menu__item {
   font-weight: 500;
}
/*altura linha menu*/
div>.header__nav--full {
    padding-top: 20px;
}
div>.header__nav--full:before {
    margin-top: -8px;
}
/*fundo pesquisa*/
.header__search-input {
    background: #0000000a;
}
main .header__search {
    border-bottom: 0;
    background: 0;
}
/*espaço superior menu*/
.header__container {
    margin-bottom: 0;
}
/*linha horizontal estante produts*/
main .products-carousel__title:before {
    display: inline;
    position: inline;
    background: #00000061;
    height: 1px;
    margin-top: 10px;
}
.menu__link {
    background: #ff0d72;
    border-radius: 10px;
    font-weight: 500;
    color: #ffffff;
    border-top: 0;
}
.gallery__thumbs {
    margin: 0 !important;
}
@media (max-width: 575.98px){
main .product__buy .quantity-selector__btn-container {
    display: inherit;
    margin-left: 0;
    margin-right: 10px;
    border-radius: 10px;
    height: 55px;
}
  main .product__buy .btn-lg{
  height: 55px;
  }
  .quantity-selector__input {
    padding-top: 20px;
  }
}

/*reposicionando avise me*/
.product__out-of-stock, .product__unavailable {
    margin-top: 24px;
}
/*centralizar texto categorias*/
.page-title.container.mt-4.mb-4.mb-lg-5 {
    text-align: center;
    font-family: sans-serif;
    padding-top: 20px;
}
.page-title__short-description.lead {
    font-size: 15px;
    padding-bottom: 10px;
}
main .page-title__head h1 {
    font-family: sans-serif;
    line-height: 20px;
    font-size: 22px;
    font-weight: 600;
}
.col-12.col-lg-2.mb-3.mb-lg-0 {
    max-width: 25%;
    margin: auto;
    padding-bottom: 20px;
}

@media (min-width: 768px){
.page-title__head {
    display: block;
 }
  .page-title.container.mt-4.mb-4.mb-lg-5 {
    padding-top: 50px;
  }
  .col-12.col-lg-2.mb-3.mb-lg-0 {
    max-width: 12%;
  }
}
/*background rodape*/
main .footer {
    background-color: #000000;
}
/*opacidade ao passar mouse categorias*/
.col-12.col-lg-2.mb-3.mb-lg-0:hover {
    opacity: 50%;
}
/*ajustes botão adicionar favoritos*/
a.btn.btn-sm.product__favorite {
    color: black;
    padding: 3px 7px;
    font-size: 18px;
    background-color: #f7f7f7;
    border: solid 1px #dfdfdf;
}
a.btn.btn-sm.product__favorite:hover {
    opacity: 60%;
}
.product__short-stock {
    display: none;
}
.variations__grid--colors button {
    font-size: 1rem !important;
}
/*remove texto frete gratis a parti de tanta und*/
.product__free-shipping-from {
    display: none;
}
/*tornar visivel icone favorito*/
a.btn.product-card__favorite.fade {
    opacity: 100%;
    color: black;
}
/*remover opções de pagamento pagina produto*/
main .product #product-actions>div article {
    display: none;
}
/*fundo logo ecom rodape*/
main .ecom-credits {
    background-color: #000000;
}
/*ajuste checkout e frete*/
a.payment__gateway.list-group-item.list-group-item-action {
    background-color: #f0f0f0;
    margin: 10px 0;
    border: 0;
    border-radius: 8px;
    color: black;
}


.list-group-item-action.list-group-item {
    color: #000000;
    margin: 6px 0;
    border-radius: 8px;
    background: #f0f0f0;
    border: 0;
}

a.list-group-item.list-group-item-action.active {
    background: #ff0d72;
    color: white;
    margin: 6px 0;
}

/*reduzir tamanho pagina espaçamentos*/
ol.breadcrumb.my-2.my-sm-3 {
    margin-bottom: -30px;
}

p.product__prices {
    margin-bottom: -8px;
}
main .product__prices {
    padding-bottom: 0;
}

main .product .shipping-calculator {
    padding-top: 0;
}

/*posicao popup tela inicial*/
.modal.show .modal-dialog {
    margin-top: 50px;
}

/*produtos recomendados*/
.recommended-items .product-card__buy {
    	display: contents !important;
}

.recommended-items .btn-sm {
	padding: 10px 30px;
}
.row.no-gutters {
    margin: 20px;
}

#cart .row .recommended-items {
    border: 1px solid #ced4da;
}
.recommended-items__title {
    margin-top: -1px;
    background: #e7e7e7;
}

.cart__list + .recommended-items .recommended-items__title .lead {
 font-size: 0
}

.cart__list + .recommended-items .recommended-items__title .lead:after {
 font-size: 20px !important;
 content: "Aproveite o frete e adicione:"
}

/*botao prosseguir pagamento*/
.btn-success {
    width: 100%;
    font-weight: 500;
    text-transform: uppercase;
    line-height: 36px;
}
a.cart__btn-checkout.btn.btn-lg.btn-block.btn-success {
    text-transform: capitalize;
    font-size: 18px;
}

/*endereço frete*/
.summary__amount__row>span small {
    display: none !important;
}

/*texto retirar na loja*/
.shipping-line[data-service-code=PICKUP]+small {
    font-size: 14px;
    font-weight: 600;
}
